import {useRef} from 'react';
//import logo from './logo.svg';
import './App.css';
import * as tf from "@tensorflow/tfjs";
import * as faceLandmarksDetection from '@tensorflow-models/face-landmarks-detection';
//at one point i was suppose to use facemesh but this no longer is supported
//import * as facemesh from "@tensorflow-models/facemesh";
import Webcam from "react-webcam";
import {drawMesh} from "./utilities"


function App() {
  //set up references
  const webcamRef = useRef(null);
  const canvasRef = useRef (null);

  // load facemesh
  /*const runFacemesh = async () =>{
    console.log('hi')
    const net = await faceLandmarksDetection.load(faceLandmarksDetection.SupportedPackages.mediapipeFacemesh);
    setInterval(()=>{
      detect(net);
    },1500)
  };

  // detect function
  const detect = async(net) =>{
    //check to make sure webcame is ready for detection
    if(
      typeof webcamRef.currrent !== "undefined" && webcamRef.current !== null && webcamRef.current.video.readyState===4){
        //get video properties
        const video = webcamRef.current.video;
        const videoWidth = webcamRef.current.video.videoWidth;
        const videoHeight = webcamRef.current.video.videoHeight;
        //set video width
        webcamRef.current.video.width = videoWidth;
        webcamRef.current.video.height = videoHeight
        //set canvas width
        canvasRef.current.width = videoWidth;
        canvasRef.current.height = videoHeight;
        //make detections
        const face = await net.estimateFaces(video);
        if (face){
          console.log(face)
        }
        // get canvas context for drawing
      }
  }

  runFacemesh();
  */
  async function main() {
    // Load the MediaPipe Facemesh package.
    if (webcamRef.current){
      console.log(true)
    const model = await faceLandmarksDetection.load(
      faceLandmarksDetection.SupportedPackages.mediapipeFacemesh);

    // Pass in a video stream (or an image, canvas, or 3D tensor) to obtain an
    // array of detected faces from the MediaPipe graph. If passing in a video
    // stream, a single prediction per frame will be returned.
    const predictions = await model.estimateFaces({
      input: document.querySelector("video"), predictIrises:true
    });
    predictions.forEach(prediction=>{
      console.log(prediction.scaledMesh)
    }
    )
    if (predictions.length > 0) {
      const ctx = canvasRef.current.getContext("2d")
    drawMesh(predictions, ctx)
      /*
      `predictions` is an array of objects describing each detected face, for example:

      [
        {
          faceInViewConfidence: 1, // The probability of a face being present.
          boundingBox: { // The bounding box surrounding the face.
            topLeft: [232.28, 145.26],
            bottomRight: [449.75, 308.36],
          },
          mesh: [ // The 3D coordinates of each facial landmark.
            [92.07, 119.49, -17.54],
            [91.97, 102.52, -30.54],
            ...
          ],
          scaledMesh: [ // The 3D coordinates of each facial landmark, normalized.
            [322.32, 297.58, -17.54],
            [322.18, 263.95, -30.54]
          ],
          annotations: { // Semantic groupings of the `scaledMesh` coordinates.
            silhouette: [
              [326.19, 124.72, -3.82],
              [351.06, 126.30, -3.00],
              ...
            ],
            ...
          }
        }
      ]
      */

      for (let i = 0; i < predictions.length; i++) {
        const keypoints = predictions[i].scaledMesh;

        // Log facial keypoints.
        for (let i = 0; i < keypoints.length; i++) {
          const [x, y, z] = keypoints[i];

          console.log(`Keypoint ${i}: [${x}, ${y}, ${z}]`);
        }
      }
    }
  }
  }
  setInterval(()=>{
    main();
  },100)

  main();
  return (
    <div className="App">
      <header className="App-header">
      <Webcam ref={webcamRef} style={
        {
        position:"absolute",
        marginLeft: "auto",
        marginRight: "auto",
        left: 0,
        right: 0,
        textAlign: "center",
        zIndex: 9,
        width: 640,
        height: 480
        }
      }/>
      <canvas ref={canvasRef}
      style={
        {
          position:"absolute",
          marginLeft: "auto",
          marginRight: "auto",
          left: 0,
          right: 0,
          textAlign: "center",
          zIndex: 9,
          width: 640,
          height: 480
          }
      } />
      </header>
    </div>
  );
}

export default App;
